INSERT INTO `session` VALUES ('3vjg37r0sph74ua7gq4pknfar4', '1360961002', '3600', 'Default|a:13:{s:11:\"initialized\";b:1;s:6:\"errors\";s:0:\"\";s:14:\"successmessage\";s:0:\"\";s:10:\"formvalues\";a:0:{}s:6:\"userid\";s:2:\"13\";s:9:\"firstname\";s:5:\"James\";s:8:\"lastname\";s:6:\"Mattix\";s:5:\"email\";s:24:\"james.mattix@atechra.com\";s:3:\"arq\";s:709:\"SELECT t.status, t.id, td.employeeid, td.projectid, td.clientid, DATE_FORMAT(t.weekendingdate, \'%d %b, %Y\') as \'Week Ending\', CONCAT(u.firstname, \' \', u.lastname) AS `Employee`, CONCAT(DATE_FORMAT(SUBDATE( t.weekendingdate, INTERVAL 4 DAY),\'%d %b, %Y\'), \' - \', DATE_FORMAT(t.weekendingdate,\'%d %b, %Y\')) AS `Period`, SUM(td.hours) AS `Total Hours`, lv.lookupvaluedescription AS `Status` FROM timesheet AS t INNER JOIN useraccount AS u ON t.employeeid = u.id INNER JOIN timesheetdetail AS td ON t.id = td.timesheetid INNER JOIN lookuptypevalue AS lv ON (t.`status` = lv.lookuptypevalue AND lv.lookuptypeid = 6)   AND t.employeeid = \'13\'  GROUP BY t.employeeid, t.weekendingdate  ORDER BY t.weekendingdate DESC \";s:3:\"crq\";s:722:\"SELECT t.status, t.id, td.employeeid, td.projectid, td.clientid, DATE_FORMAT(t.weekendingdate, \'%d %b, %Y\') as \'Week Ending\', CONCAT(u.firstname, \' \', u.lastname) AS `Employee`, CONCAT(DATE_FORMAT(SUBDATE( t.weekendingdate, INTERVAL 4 DAY),\'%d %b, %Y\'), \' - \', DATE_FORMAT(t.weekendingdate,\'%d %b, %Y\')) AS `Period`, SUM(td.hours) AS `Total Hours`, lv.lookupvaluedescription AS `Status` FROM timesheet AS t INNER JOIN useraccount AS u ON t.employeeid = u.id INNER JOIN timesheetdetail AS td ON t.id = td.timesheetid INNER JOIN lookuptypevalue AS lv ON (t.`status` = lv.lookuptypevalue AND lv.lookuptypeid = 6)   AND t.employeeid = \'13\'  GROUP BY t.employeeid, t.weekendingdate  ORDER BY t.weekendingdate DESC   LIMIT 0, 10\";s:29:\"list_query_stringstatusreport\";a:3:{s:10:\"controller\";s:12:\"statusreport\";s:6:\"action\";s:4:\"list\";s:6:\"module\";s:7:\"default\";}s:12:\"statusreport\";s:14055:\"<html><head><meta http-equiv=\"Content-Type\" content=\"text/html; charset=iso-8859-15\"><style type=\"text/css\">body, select, textarea, button, input, td {\nfont-family: Arial, Helvetica, Tahoma, sans-serif;	\nfont-size:14px;\ncolor:#555555;\nline-height: 1.2em;\n}</style></head><body><style type=\"text/css\">\ntable.formtable {\n	border-left:solid 1px #FFFFFF; color:\n	border-bottom:solid 1px #FFFFFF;\n	border-right:solid 1px #FFFFFF;\n	border-top:solid 1px #FFFFFF;\n}\ntable.formtable td {\n	border-left:solid 1px #FFFFFF;\n	border-bottom:solid 1px #FFFFFF;\n	border-right:solid 1px #FFFFFF;\n	border-top:solid 1px #FFFFFF;\n}\n.heading {\n	font-size:16px;		\n}\ntable.formtable td.label {\n	vertical-align:top;\n}\n</style>\n<table class=\"formtable\">\n  <thead>\n    <tr>\n      <td colspan=\"2\">View Status Report</td>\n    </tr>\n  </thead>\n      <tr>\n    <td class=\"label\">Employee:</td>\n    <td>James Mattix</td>\n  </tr>\n  <tr>\n    <td class=\"label\">Project:</td>\n    <td>Project Management 1</td>\n  </tr>\n  <tr>\n    <td class=\"label\">Week Ending:</td>\n    <td>02/15/2013</td>\n  </tr>\n  <tr>\n    <td class=\"label\">Approval Status:</td>\n    <td>Submitted</td>\n  </tr>\n  <tr>\n    <td class=\"label\">Activities this period:</td>\n    <td><p>&nbsp;</p><br />\r\n<p style=\"text-align: center; line-height: 12.6pt;\"><strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">Portfolio Management</span></strong></p><br />\r\n<p style=\"line-height: 12.6pt; text-align: left;\"><strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">Note: &nbsp;</span></strong><span style=\"font-size: 10.5pt;\">The non-bolded descriptive passages below are taken typically from the project\'s Investment Profile and are only edited for grammar. They will not be repeated each week. &nbsp;Bolded copy is current activity.</span></p><br />\r\n<p style=\"line-height: 12.6pt;\"><strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">Alaska Land Information System (ALIS):</span></strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\"> ALIS stores and manages lands and minerals data.&nbsp; Information is abstracted from official lands and minerals cases.&nbsp; Certain retrievals from this system are linked to scanned images of Patent documents, Master Title Plats, and Survey Plats and Notes.&nbsp; Alaska&rsquo;s conveyance effort is a multi-million dollar project with high visibility and Congressional oversight, This critical activity relies heavily on ALIS being in place and working properly.&nbsp; ALIS is currently at a high risk of failing if it remains on the same out-dated technology platform.&nbsp; &nbsp;<strong>Brief meeting with WO590 personnel to discuss security status of old and new systems. &nbsp;We agreed to hold a larger meeting next week, upon Ms. Bowers &nbsp;and Mr. Aslam\'s return.</strong></span></p><br />\r\n<p style=\"line-height: 12.6pt;\"><strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">Bureau Application Security System (BASS):&nbsp; This little program is more of a tool than a project.&nbsp; It is the signon front end for BLM&rsquo;s national applications.&nbsp; It has been in steady-state O&amp;M for a number of years.&nbsp; The only time there is activity is when a new Application is brought on line, or a new browser is certified (as in the case of the recent Chrome upgrade).&nbsp; Virtually no ongoing O&amp;M costs associated with BASS.&nbsp; No activities this period</span></strong></p><br />\r\n<p style=\"line-height: 12.6pt;\"><strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">Cadastral Automated Request System (CARS):&nbsp; This program is used by field surveyors to request surveys of Indian lands.&nbsp; It goes to a single user in Portland, who schedules and tracks the surveys.&nbsp; It has been in steady state for years and is basically not maintained, no associated costs.&nbsp; No activities this period.</span></strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">&nbsp;</span></p><br />\r\n<p style=\"line-height: 12.6pt;\"><strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">Document Tracking System (DTS): </span></strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">&nbsp;Use of DTS enables BLM to implement one very low-cost system already in use throughout much of the Department, thereby increasing the efficiency and interoperability within the Department\'s bureaus and offices, as well as between the state offices and headquarters.&nbsp; The system&rsquo;s ability to surname documents electronically within the BLM eliminates lost documentation and re-work, provides visibility and tracking for documents with deadlines, aids in locating records during a document search, and eliminates the time necessary to transmit document packages and interpret hand-written notes.&nbsp; Through DTS, all comments and edits are tracked and recorded by name and advance search and report capabilities are available.&nbsp; The DTS is web-based and provides the manager flexibility for surnaming documents while away from the office.&nbsp; The benefits of these enhancements (no lost records, increased accountability and transparency, and long-distance accessibility) are determined to exceed the $20K investment.&nbsp;<strong> DTS came on line as of December 31, 2012.&nbsp; System owner (WO600) is conducting training sessions for Bureau personnel.&nbsp; No activity since Investment Profile was turned in a/o 1/31.</strong></span></p><br />\r\n<p style=\"line-height: 12.6pt;\"><strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">Electronic Records Management System ERMS:&nbsp; This is a ghost program.&nbsp; It has been budgeted annually for many years but has never been funded and was never built,&nbsp; We are researching the method for deleting this and several other ghosts from the CPIC records.</span></strong></p><br />\r\n<p style=\"line-height: 12.6pt;\"><strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">Enterprise Geographic Information System (eGIS):&nbsp; </span></strong><span style=\"font-size: 11.0pt; mso-bidi-font-size: 10.0pt;\">The EGIS project is a non-traditional data management IT project that provides a platform (National Geospatial Server)&nbsp;for the hosting of the BLM&rsquo;s corporate geospatial data such as; Wild Horse and Burros Herd Management Areas, Invasive Species Areas, Range Allotments, NLCS Boundaries, Land Use Planning Boundaries etc.&nbsp; EGIS also is designed to provide a host environment to mini applications or tools&nbsp;that are designed around a geospatial software core. <strong>According to the PM, this program is being rebaselined and the new quarterly Investment Profile cannot be completed at this time.</strong></span></p><br />\r\n<p style=\"line-height: 12.6pt;\"><strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">General Land Office Automated Records System (GLO):</span></strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\"> &nbsp;GLO encompasses the automation and retrieval of eastern and western land title information, patent images, cadastral survey plats and field notes, and MTP/HI data.&nbsp; The GLO provides the public a means to perform searches for land patents by name.&nbsp; In addition, the GLO is a preservation process for the aging land documents.&nbsp; The GLO provides a web-based access for query, image viewing, and e-commerce of the Federal land Record&nbsp; The intended customer for this system are major genealogical groups, companies, and private citizens, enabling them to research land title and view the patent image. <strong>NOC engineering performed a server upgrade last week that resulted in a marked improvement in performance. (Around noon daily, the system would become exceedingly slow.&nbsp; As the west coast came on line, the old server simply could not handle the traffic volume.) &nbsp;Also added significant additional storage capacity and this week they are installing a backup/archiving system, securing the GLO data for the first time.&nbsp; The GLO quarterly Investment Profile was submitted on time on 1/31/13.</strong></span></p><br />\r\n<p style=\"line-height: 12.6pt;\"><strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">GeoCommunicator (GC):&nbsp;</span><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\"> GC was t</span></strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\"><strong>he publishing engine for the now-defunct NILS, GeoCommunicator also publishes data from multiple LR2000 databases, and those of AFMSS, ALIS, FAMS, GLO, NISIMS, PRIS, RAS, REA and WHBP.&nbsp; GeoCommunicator uses old, outdated technology that is expensive to maintain and will eventually be replaced by the planned Geospatial Publication Module (GPM). GC is in steady state Operation and Maintenance condition.&nbsp; No activities this period.</strong></span></p><br />\r\n<p style=\"line-height: 12.6pt;\"><strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">Geographic Coordinate Data Base Data Preparation System (GCDB):</span></strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">&nbsp; GCDB Data Prep is conversion software used to collect the GCDB and associated land descriptions. GCDB Data Prep modules identify data errors, provide interactive spatial tools to correct the data and convert the GCDB data to GIS format.&nbsp; &nbsp;Funding for GCDB Data Prep software operations and maintenance was authorized in March 1998 and this application has been in steady state since then.&nbsp; <strong>No activity this period.</strong></span></p><br />\r\n<p style=\"line-height: 12.6pt;\"><strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">Geospatial Publication Module (GPM):&nbsp; </span></strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">This project is in the planning phase for becoming the replacement for GeoCommunicator.&nbsp; <strong>ITIB last week gave authorization to move forward with an RFP and agreed to allocate $900K toward the project.&nbsp; Reviewed activities in the Monday biweekly meeting and provided business case template to IRM Advisor. Reviewed the consultant&rsquo;s proposal for creating the RFP.&nbsp; &nbsp;Offered a couple of suggestions for inclusion in the SOW.</strong></span></p><br />\r\n<p style=\"line-height: 12.6pt;\"><strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">Information Technology Asset Management System (ITAMS):&nbsp;</span></strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\"> </span><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\"><strong>This is another ghost program that has been budgeted for years, but never funded, no work nor funds expended.&nbsp; It is now a placeholder for a WO500 strategic initiative and a study is currently being activated by WO 590 Information Assurance personnel.&nbsp;</strong> <strong>No activities this period.</strong></span></p><br />\r\n<p style=\"line-height: 12.6pt;\"><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\"><strong><br /></strong></span></p><br />\r\n<p style=\"line-height: 12.6pt; text-align: left;\"><strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">Data Center Consolidation Initiative (IT Transformation): &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></strong><span style=\"font-size: 10.5pt;\"><strong>No activities this period</strong>.</span></p><br />\r\n<p style=\"line-height: 12.6pt;\">&nbsp;</p><br />\r\n<p style=\"line-height: 12.6pt;\"><strong style=\"line-height: 12.6pt; text-indent: 0.5in;\"><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">Integrated Lifecycle Management (ILM) Handbook:</span></strong></p><br />\r\n<p style=\"line-height: 12.6pt;\"><strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">Completed a Work Breakdown Structure (WBS) graphic outlining deliverable artifacts by ILM Phase.&nbsp;Meeting the last week in February with NOC representatives to review their consolidated comments, final meeting before publication.</span></strong></p><br />\r\n<p style=\"line-height: 12.6pt;\"><strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\">&nbsp;</span></strong></p><br />\r\n<p style=\"line-height: 12.6pt;\">&nbsp;</p><br />\r\n<p>&nbsp;</p></td>\n  </tr>\n  <tr>\n    <td class=\"label\">Accomplishments:</td>\n    <td><p><strong>Portfolio Management: &nbsp;</strong>Completed<strong>&nbsp;</strong>a more comprehensive listing of BLM systems to be to be tracked in the IT Portfolios and delivered to the Portfolio Managers. &nbsp;Using a couple of reports from the NOC, identified some 30 systems that are not currently&nbsp;being tracked. It may be that some, e.g. SharePoint, are accounted for in Infrastructure. &nbsp;.</p><br />\r\n<p><strong>GPM: </strong>Created a graphical WBS representation of the &nbsp;deliverable artifacts by ILM&nbsp;&nbsp;phase and transmitted to the consultant that will be producing the RFP for the Geospatial Publication Module.</p><br />\r\n<p>&nbsp;</p><br />\r\n<p>&nbsp;</p></td>\n  </tr>\n  <tr>\n    <td class=\"label\">Meetings and Presentations:</td>\n    <td><p><strong>GPM: &nbsp;</strong>Biweekly conference call reviewed the proposal from the consultant who will draft the RFP and determined next steps forward after receiving authority for the RFP from ITIB last week.</p><br />\r\n<p><strong>DCCI: </strong>&nbsp;Weekly call cancelled due to travel schedules.</p></td>\n  </tr>\n  <tr>\n    <td class=\"label\">Issues or Problems:</td>\n    <td><p>None.</p></td>\n  </tr>\n  <tr>\n    <td class=\"label\">Items that need the attention of the COTR:</td>\n    <td><p>N/A</p></td>\n  </tr>\n  <tfoot>\n    <tr>\n      <td>&nbsp;</td>\n      <td>                                                <a href=\"/statusreport/list\" class=\"positiveaction\" title=\"Status Reports List\"><img src=\"/images/arrow_undo.png\" />Status Reports List</a>\n                </td>\n    </tr>\n  </tfoot>\n</table>\n</body></html>\";s:26:\"list_query_stringtimesheet\";a:3:{s:10:\"controller\";s:9:\"timesheet\";s:6:\"action\";s:4:\"list\";s:6:\"module\";s:7:\"default\";}}');
