INSERT INTO `session` VALUES ('c5f7vbvf7fc3ngp45ckn3ctlk2', '1360962067', '3600', 'Default|a:14:{s:11:\"initialized\";b:1;s:6:\"errors\";s:0:\"\";s:14:\"successmessage\";s:0:\"\";s:10:\"formvalues\";a:0:{}s:6:\"userid\";s:2:\"22\";s:9:\"firstname\";s:5:\"Cathy\";s:8:\"lastname\";s:5:\"Terry\";s:5:\"email\";s:23:\"cathy.terry@atechra.com\";s:3:\"arq\";s:552:\"SELECT sr.id, sr.employeeid, sr.projectid, sr.clientid, sr.status, CONCAT(u.firstname, \' \', u.lastname) AS `Employee`, DATE_FORMAT(sr.weekendingdate, \'%m/%d/%Y\') as \'Week Ending\', p.title AS Project, c.name as Client, lv.lookupvaluedescription AS `Status`  FROM statusreport AS sr INNER JOIN client AS c ON sr.clientid = c.id INNER JOIN project AS p ON sr.projectid = p.id INNER JOIN lookuptypevalue lv ON (sr.status = lv.lookuptypevalue AND lv.lookuptypeid = 6) INNER JOIN useraccount AS u WHERE sr.employeeid = u.id   ORDER BY sr.weekendingdate DESC \";s:3:\"crq\";s:565:\"SELECT sr.id, sr.employeeid, sr.projectid, sr.clientid, sr.status, CONCAT(u.firstname, \' \', u.lastname) AS `Employee`, DATE_FORMAT(sr.weekendingdate, \'%m/%d/%Y\') as \'Week Ending\', p.title AS Project, c.name as Client, lv.lookupvaluedescription AS `Status`  FROM statusreport AS sr INNER JOIN client AS c ON sr.clientid = c.id INNER JOIN project AS p ON sr.projectid = p.id INNER JOIN lookuptypevalue lv ON (sr.status = lv.lookuptypevalue AND lv.lookuptypeid = 6) INNER JOIN useraccount AS u WHERE sr.employeeid = u.id   ORDER BY sr.weekendingdate DESC   LIMIT 0, 10\";s:30:\"list_query_stringcorporatefile\";a:4:{s:10:\"controller\";s:13:\"corporatefile\";s:6:\"action\";s:4:\"list\";s:8:\"parentid\";s:2:\"36\";s:6:\"module\";s:7:\"default\";}s:18:\"custom_failure_url\";s:44:\"L2NvcnBvcmF0ZWZpbGUvaW5kZXgvZm9sZGVyaWQvMzY=\";s:29:\"list_query_stringstatusreport\";a:3:{s:10:\"controller\";s:12:\"statusreport\";s:6:\"action\";s:4:\"list\";s:6:\"module\";s:7:\"default\";}s:12:\"statusreport\";s:22159:\"<html><head><meta http-equiv=\"Content-Type\" content=\"text/html; charset=iso-8859-15\"><style type=\"text/css\">body, select, textarea, button, input, td {\nfont-family: Arial, Helvetica, Tahoma, sans-serif;	\nfont-size:14px;\ncolor:#555555;\nline-height: 1.2em;\n}</style></head><body><style type=\"text/css\">\ntable.formtable {\n	border-left:solid 1px #FFFFFF; color:\n	border-bottom:solid 1px #FFFFFF;\n	border-right:solid 1px #FFFFFF;\n	border-top:solid 1px #FFFFFF;\n}\ntable.formtable td {\n	border-left:solid 1px #FFFFFF;\n	border-bottom:solid 1px #FFFFFF;\n	border-right:solid 1px #FFFFFF;\n	border-top:solid 1px #FFFFFF;\n}\n.heading {\n	font-size:16px;		\n}\ntable.formtable td.label {\n	vertical-align:top;\n}\n</style>\n<table class=\"formtable\">\n  <thead>\n    <tr>\n      <td colspan=\"2\">View Status Report</td>\n    </tr>\n  </thead>\n      <tr>\n    <td class=\"label\">Employee:</td>\n    <td>Chintan Mody</td>\n  </tr>\n  <tr>\n    <td class=\"label\">Project:</td>\n    <td>Project Management 2</td>\n  </tr>\n  <tr>\n    <td class=\"label\">Week Ending:</td>\n    <td>02/15/2013</td>\n  </tr>\n  <tr>\n    <td class=\"label\">Approval Status:</td>\n    <td>Submitted</td>\n  </tr>\n  <tr>\n    <td class=\"label\">Activities this period:</td>\n    <td><p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">ESRS:&nbsp; </span></strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">ESRS is a project planning and tracking system that evaluates the effectiveness of funds spent on post-fire Emergency Stabilization and Rehabilitation activities.&nbsp; It provides managers the ability to share data on a multi-level basis for initial plan submission, review, audit and reporting.<strong>&nbsp; For past one month, I am working with Mary Frye and Roy King to get Investment Profile (IP) updated for the FY2013 Q1.&nbsp; IP was submitted on 2/4/2013 for WO-570&rsquo;s review.&nbsp; I have reviewed the IP and started to track missing information or discrepancies.&nbsp; I am finding issue with Earned Value Management (EVM) and some other issues during my review.&nbsp; I will review these data with Mohammad (WO-570 Portfolio Manager) in our weekly Project Status meeting.</strong></span></p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">&nbsp;</span></strong></p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">FORVIS: </span></strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">The Forest Vegetation Information System (FORVIS)) is used for storage, retrieval, and analysis of data about forest vegetation polygons for planning and site-specific project NEPA documents.&nbsp; The system provides data management and analytical capabilities for inventorying and monitoring vegetation on forested uplands.&nbsp; <strong>Investment Profile (IP) was submitted on 2/4/2013 for WO-570&rsquo;s review.&nbsp; I will review the IP in next few weeks.</strong></span></p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">&nbsp;</span></strong></p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">NISIMS:&nbsp; </span></strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">The module provides tools for data collection and the generation of analysis and statistics for noxious weed infestations and treatments through a centralized weed geodatabase.&nbsp; This \"toolbar\" is being added to current COTS ESRI application that is Bureau-wide.&nbsp; It was started in 2003.&nbsp; I<strong>nvestment Profile (IP) was submitted on 2/4/2013 for WO-570&rsquo;s review.&nbsp; No major activity happening on this project.</strong></span></p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">&nbsp;</span></strong></p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">PRIS: </span></strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">The Protection and Response Information System (PRIS) provides a repository of hazardous material, historic abandoned mine and Environmental and Disposal Liability sites that pose likely safety risks to the public or present serious threats to the environment.&nbsp; PRIS consists of Abandoned Mines and HazMat cleanup sites, and also contains BLM\'s Environmental and Disposal Liability sites.&nbsp; PRIS combines the Abandoned Mine Information System (AMIS) and Site Cleanup System.&nbsp; I<strong>nvestment Profile (IP) was submitted on 2/5/2013 for WO-570&rsquo;s review.&nbsp; I will review the IP in next few weeks.</strong></span></p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">&nbsp;</span></p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">RAS:&nbsp; </span></strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">The Rangeland Administration System (RAS) is a module of the BLM Use Authorization System (UAS), a platform independent, web-based business system utilizing a centralized database for processing and monitoring public land use authorizations.&nbsp; UAS-RAS maintains information about authorizations, allotments, operators, and reports on grazing resources, and grazing bill history. RAS is a legacy system in Operations and Maintenance (O&amp;M).&nbsp; I<strong>nvestment Profile (IP) was submitted on 2/5/2013 for WO-570&rsquo;s review.&nbsp; I will review the IP in next few weeks.</strong></span></p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">&nbsp; </span></p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">RIPARIAN:&nbsp; Riparian is in the final quality testing and will be going to Operation and maintenance in next month or so.&nbsp; RIPARIAN doesn&rsquo;t have an investment profile.&nbsp; I talked to Roy King (NOC Project Manager) about creating an investment profile (IP) and found out he is retiring next month.&nbsp; He would pass the IP work to next Project Manager for the IP work.</span></strong></p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">&nbsp;</span></strong></p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">RIPS: </span></strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">The Rangeland Improvement Project System (RIPS) is a legacy system which provides management data regarding rangeland improvement projects across BLM sites, such as spring developments, fences, pipelines, reservoirs, wells, cattle guard installations, enclosures, land and vegetation treatments, and similar features.&nbsp;&nbsp;&nbsp; The system is currently in a steady state status, encompassing Operations and Maintenance activities only.&nbsp; No major update to report. I<strong>nvestment Profile (IP) was submitted on 2/5/2013 for WO-570&rsquo;s review.&nbsp; I will review the IP in next few weeks.</strong></span></p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">&nbsp;</span></strong></p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">RMIS: </span></strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">The Recreation Management and Information System (RMIS) is a BLM-internal application, in Operations and Maintenance phase, that aggregates information pertaining to BLM land used for public recreation purposes.&nbsp; Since 1984, the Recreation Management Information System (RMIS) has been the official record for outdoor recreation information on public lands managed by the BLM.&nbsp; RMIS is used to report statistics about public visitation permit sales, site usage, off-highway vehicle designations, back country byway information, and partnerships and agreements.&nbsp; The vehicle designations and byway information is essentially roads and trails that are referenced by name only.&nbsp; The system has data elements with lat/long information, but currently the information isn\'t used for GIS mapping. It tracks the name of recreation areas, acreage, primary purpose of the site (camping, hiking trail, day-use site, interpretive center, and some descriptive text.&nbsp; It has business rules that calculate the amount of certain types of recreation use based on a percentage of the number of yearly visitors.&nbsp; .&nbsp; I<strong>nvestment Profile (IP) was submitted on 2/5/2013 for WO-570&rsquo;s review.&nbsp; I will review the IP in next few weeks.</strong></span></p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">&nbsp;</span></strong></p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">&nbsp;</span></strong></p><br />\r\n<p class=\"MsoNormal\" style=\"line-height: 12.6pt; background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-size: 10.5pt; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;; color: #222222;\">SPATS:</span></strong><span style=\"font-size: 10.5pt; font-family: Arial, sans-serif;\"></span></p><br />\r\n<p class=\"MsoNormal\" style=\"line-height: 12.6pt; background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-size: 10.5pt; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;; color: #222222;\">I met with Business Group (WO-200) to get their input on the few questions that I had regarding the Project Charter for the SPATS.&nbsp; We met two separate occasions in the week to get more information about the projects.</span></strong></p><br />\r\n<p class=\"MsoNormal\" style=\"line-height: 12.6pt; background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-size: 10.5pt; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;; color: #222222;\">The business case for the SPATS is about 75% complete.&nbsp; I have contacted the Enterprise Architecture (EA) and Security and Privacy officer for their input to complete the business.&nbsp; I have updated Project Overview, Scope, Business Goals and Objectives, Organizations impact and project assumptions in the SPATS Project Charter. </span></strong></p><br />\r\n<p class=\"MsoNormal\" style=\"line-height: 12.6pt; background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-size: 10.5pt; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;; color: #222222;\">&nbsp;</span></strong></p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-size: 10.5pt; line-height: 115%; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;; color: #222222;\">WHBPS:&nbsp; </span></strong><span style=\"font-size: 10.5pt; line-height: 115%; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;; color: #222222; mso-bidi-font-weight: bold;\">The Wild Horse and Burro Program System (WHBPS) is the system of record for animal and herd management.&nbsp; WHBPS is a web-based intranet application supporting all business processes of the Wild Horse and Burro Program.&nbsp; </span><strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">For past one month, I am working with Mary Frye and Joe Manke to get Investment Profile (IP) updated for the FY2013 Q1.&nbsp; IP was submitted on 2/4/2013 for WO-570&rsquo;s review.&nbsp; I have reviewed the IP and started to track missing information or discrepancies.&nbsp; I found some issues during my review.&nbsp; I will review these data with Mohammad (WO-570 Portfolio Manager) in our weekly Project Status meeting.</span></strong></p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">&nbsp;</span></strong></p><br />\r\n<p class=\"MsoNormal\" style=\"background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-family: Arial, sans-serif; color: #222222; background-position: initial initial; background-repeat: initial initial;\">IT Transformation (ITT):&nbsp; I met with Steward Nelson (ITT Lead) to discuss how Mission Critical Business inventory analysis could help with his efforts.&nbsp; He would be inviting to the meeting for ITT starting next week.&nbsp; ITT&rsquo;s goal is to save $500 million in taxpayer dollars by 2020.</span></strong></p><br />\r\n</p></td>\n  </tr>\n  <tr>\n    <td class=\"label\">Accomplishments:</td>\n    <td><p><br />\r\n<p class=\"MsoNormal\"><strong><span style=\"font-size: 10.5pt; line-height: 115%; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;;\">ESRS:</span></strong><span style=\"font-size: 10.5pt; line-height: 115%; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;;\">&nbsp; I have started to review Investment Profile (IP) for this application.&nbsp; I am finding much information is missing or obsolete.&nbsp; I am keep track of this issue and will present my finding to Mohammad in my next status meeting.</span></p><br />\r\n<p class=\"MsoNormal\"><strong><span style=\"font-size: 10.5pt; line-height: 115%; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;;\">WHBPS: &nbsp;</span></strong><span style=\"font-size: 10.5pt; line-height: 115%; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;;\">I have started to review Investment Profile (IP) for this application.&nbsp; For this application, majority information is updated. &nbsp;However, I am keep track of this issue and will present my finding to Mohammad in my next status meeting.</span></p><br />\r\n<p class=\"MsoNormal\"><strong><span style=\"font-size: 10.5pt; line-height: 115%; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;;\">SPATS:</span></strong></p><br />\r\n<p class=\"MsoNormal\"><span style=\"font-size: 10.5pt; line-height: 115%; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;;\">I have updated SPATS business case and Charter draft version based on the latest information that I have received from the business group (WO-200).</span></p><br />\r\n</p></td>\n  </tr>\n  <tr>\n    <td class=\"label\">Meetings and Presentations:</td>\n    <td><p><br />\r\n<p class=\"MsoNormal\"><strong><span style=\"font-size: 10.5pt; line-height: 115%; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;;\">General:</span></strong></p><br />\r\n<p class=\"MsoNormal\"><strong><span style=\"font-size: 10.5pt; line-height: 115%; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;;\">&nbsp;</span></strong></p><br />\r\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt; line-height: normal; background-position: initial initial; background-repeat: initial initial;\"><strong><span style=\"font-size: 10.0pt; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;; color: #222222;\">&nbsp;</span></strong></p><br />\r\n<p class=\"MsoNormal\"><span style=\"font-size: 10.0pt; line-height: 115%; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;; color: #222222;\">I met with Mohammad to give status of my projects for our weekly meeting.&nbsp; In this meeting, I showed him the draft version of SPATS business case and Charter.&nbsp; </span></p><br />\r\n<p class=\"MsoNormal\"><span style=\"font-size: 10.0pt; line-height: 115%; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;; color: #222222;\">&nbsp;</span></p><br />\r\n<p class=\"MsoNormal\"><strong><span style=\"font-size: 10.0pt; line-height: 115%; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;; color: #222222;\">SPATS:</span></strong></p><br />\r\n<p class=\"MsoNormal\"><span style=\"font-size: 10.0pt; line-height: 115%; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;; color: #222222;\">I met with Neffra Matthews and Kathie Jewell to discuss Business Charter questions.</span></p><br />\r\n<p class=\"MsoNormal\"><strong><span style=\"font-size: 10.5pt; line-height: 115%; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;;\">&nbsp;</span></strong></p><br />\r\n<p class=\"MsoNormal\"><strong><span style=\"font-size: 10.5pt; line-height: 115%; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;;\">IT Transformation (ITT): &nbsp;</span></strong></p><br />\r\n<p class=\"MsoNormal\"><span style=\"font-size: 10.5pt; line-height: 115%; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; mso-fareast-font-family: &quot;Times New Roman&quot;;\">I met with Steward Nelson (ITT Lead) to discuss his plans to tackle ITT goals and how data collected so far from the Mission Critical Team can help his efforts.</span></p><br />\r\n</p></td>\n  </tr>\n  <tr>\n    <td class=\"label\">Issues or Problems:</td>\n    <td><p>n/a</p></td>\n  </tr>\n  <tr>\n    <td class=\"label\">Items that need the attention of the COTR:</td>\n    <td><p>n/a</p></td>\n  </tr>\n  <tfoot>\n    <tr>\n      <td>&nbsp;</td>\n      <td>                                        <a class=\"reject negativeaction\" href=\"/statusreport/confirmreject/statusreportid/MTY3/pgc/true/successurl/L3N0YXR1c3JlcG9ydC92aWV3L3N0YXR1c3JlcG9ydGlkL01UWTM=\" title=\"Reject Status Report\"><img src=\"/images/cross.png\" />Reject Status Report</a>\n                <a href=\"/statusreport/approve/statusreportid/MTY3/successurl/L3N0YXR1c3JlcG9ydC92aWV3L3N0YXR1c3JlcG9ydGlkL01UWTM=\" class=\"approve positiveaction\" title=\"Approve Status Report\"><img src=\"/images/tick.png\" />Approve Status Report</a>\n                        <a href=\"/statusreport/list\" class=\"positiveaction\" title=\"Status Reports List\"><img src=\"/images/arrow_undo.png\" />Status Reports List</a>\n                </td>\n    </tr>\n  </tfoot>\n</table>\n</body></html>\";}');
