INSERT INTO `statusreport` VALUES ('65', '14', '8', '1', '2012-07-06', '<p>&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"margin: 0px; padding: 0px; background-color: #fafafa;\"><span style=\"font-size: 12pt; line-height: 18px;\">Radio - Reviewed Strategic Plan to develop tactical plan framework.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin: 0px; padding: 0px; background-color: #fafafa;\">&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"margin: 0px; padding: 0px; background-color: #fafafa;\"><span style=\"font-size: 12pt; line-height: 18px;\">Vacation 7/2 through 7/4</span><span style=\"font-size: 12pt; line-height: 18px;\">&nbsp;Radio - Reviewed Strategic Plan to develop tactical plan framework.</span></p>\r\n<div><span style=\"font-size: 12pt; line-height: 18px;\"><br /></span></div>\r\n<p>&nbsp;</p>', '<p>&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"margin: 0px; padding: 0px; background-color: #fafafa; text-align: justify;\"><span style=\"font-size: 12pt; line-height: 18px;\">Radio - Reviewed Strategic Plan to develop tactical plan framework.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin: 0px; padding: 0px; background-color: #fafafa; text-align: justify;\">&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"margin: 0px; padding: 0px; background-color: #fafafa;\"><span style=\"font-size: 16.363636016845703px; line-height: 17.27272605895996px;\">Vacation 7/2 through 7/4</span></p>\r\n<p>&nbsp;</p>', '<p>&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"margin: 0px; padding: 0px; background-color: #fafafa;\"><span style=\"font-size: small;\"><span style=\"line-height: 18px;\">None.</span></span></p>\r\n<p>&nbsp;</p>', '<p>&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"margin: 0px; padding: 0px; background-color: #fafafa;\"><span style=\"font-size: 12pt; line-height: 18px;\">AFMSS - None.&nbsp;</span></p>\r\n<p><br style=\"background-color: #fafafa;\" /></p>\r\n<p class=\"MsoNormal\" style=\"margin: 0px; padding: 0px; background-color: #fafafa;\"><span style=\"font-size: 12