INSERT INTO `lookupquery` (`id`, `name`, `description`, `querystring`) VALUES
(1,	'ALL_USERS',	'Returns the list of all Users in the system',	'SELECT u.id as optionvalue, concat(u.firstname,\' \',u.lastname) as optiontext FROM useraccount u ORDER BY optiontext'),
(2,	'ALL_ACL_GROUPS',	'All defined ACL groups',	'SELECT id as optionvalue, name as optiontext FROM aclgroup'),
(3,	'ALL_RESOURCES',	'The resources that are secured within the application',	'SELECT r.name AS optiontext, r.id AS optionvalue FROM aclresource AS r ORDER BY optiontext'),
(4,	'ALL_VARIABLE_SECTIONS',	'All the variable sections in the application configuration',	'SELECT section as optiontext, section as optionvalue FROM appconfig GROUP BY section ORDER BY section'),
(5,	'ALL_CLIENTS',	'A list of all clients in the application',	'SELECT id as optionvalue, name as optiontext FROM client ORDER BY optiontext'),
(6,	'ALL_PROJECTS',	'A list of all projects in the application',	'SELECT id as optionvalue, title as optiontext FROM project ORDER BY optiontext'),
(7,	'NON_BILLABLE_PROJECTS',	'A list of all non-billable projects in the application',	'SELECT id as optionvalue, title as optiontext FROM project WHERE type = \'0\' ORDER BY optiontext '),
(8,	'BILLABLE_PROJECTS',	'A list of all billable projects in the application',	'SELECT id as optionvalue, title as optiontext FROM project WHERE type = \'1\' ORDER BY optiontext '),
(9,	'ALL_TASKS',	'A list of all projects in the application',	'SELECT id as optionvalue, name as optiontext FROM task ORDER BY optiontext');
